// src/pages/ChangePassword.jsx
import React, { useState } from "react";
import api from "../api/api";

export default function ChangePassword() {
  const [f, setF] = useState({ old_password: "", new_password: "" });
  const [msg, setMsg] = useState("");

  async function submit(e) {
    e.preventDefault();
    await api.put("/auth/change-password/", f);
    setMsg("Password changed successfully.");
    setF({ old_password: "", new_password: "" });
    setTimeout(() => setMsg(""), 2000);
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-gray-100 via-gray-50 to-gray-100 px-4">
      <div className="w-full max-w-md">
        <div className="rounded-lg shadow-xl bg-white p-8">
          <h1 className="text-xl font-bold text-center mb-6">
            Account Security
          </h1>
          <h2 className="text-lg font-semibold text-center mb-6">
            Change Password
          </h2>

          {msg && (
            <div className="mb-4 rounded-md border border-green-300 bg-green-50 text-green-700 px-3 py-2 text-sm text-center">
              {msg}
            </div>
          )}

          <form onSubmit={submit} className="space-y-4">
            <input
              type="password"
              placeholder="Old password"
              value={f.old_password}
              onChange={(e) => setF({ ...f, old_password: e.target.value })}
              className="w-full rounded-md border px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              required
            />

            <input
              type="password"
              placeholder="New password"
              value={f.new_password}
              onChange={(e) => setF({ ...f, new_password: e.target.value })}
              className="w-full rounded-md border px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              required
            />

            <button
              type="submit"
              className="w-full rounded-md bg-indigo-600 text-white font-medium py-2 hover:bg-indigo-700 transition"
            >
              Save
            </button>
          </form>

          <div className="mt-6 text-center">
            <a href="/login" className="text-sm text-indigo-600 hover:underline">
              Back to Login
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
