// src/pages/Login.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

// small helper to get the input gradient look
const GradientInput = ({ type = "text", ...props }) => (
  <div className="rounded-md bg-gradient-to-b from-[#ffffff] to-[#d9d7ee] p-[3px] shadow-[0_2px_8px_rgba(0,0,0,0.15)]">
    <input
      type={type}
      className="w-full rounded-[6px] bg-transparent px-3 py-2 text-gray-900 placeholder-gray-500 focus:outline-none"
      {...props}
    />
  </div>
);

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPwd] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, {
        username: email, // if your backend expects "username"
        password,
      });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      window.location.href = "/";
    } catch {
      setError("Invalid credentials");
    } finally {
      setLoading(false);
    }
  }

  return (
  <div
    style={{
      minHeight: "100vh",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      padding: "20px",
      background: "linear-gradient(180deg, #d7e6f2 0%, #eaf1f7 55%, #d7e6f2 100%)",
    }}
  >
    <div style={{ width: "100%", maxWidth: "400px" }}>
      {/* Card */}
      <div
        style={{
          background: "#0a0a6a",
          color: "#fff",
          borderRadius: "8px",
          boxShadow: "0 15px 35px rgba(0,0,0,0.35)",
          padding: "32px",
        }}
      >
        <h1 style={{ fontSize: "28px", fontWeight: "bold", marginBottom: "24px" }}>
          Login
        </h1>

        {error && (
          <div
            style={{
              marginBottom: "16px",
              borderRadius: "6px",
              border: "1px solid #fca5a5",
              background: "#fee2e2",
              color: "#b91c1c",
              padding: "8px 12px",
              fontSize: "14px",
            }}
          >
            {error}
          </div>
        )}

        <form onSubmit={submit} style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
          {/* Email */}
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="email"
            required
            style={{
              width: "100%",
              borderRadius: "6px",
              border: "1px solid #ccc",
              padding: "12px",
              fontSize: "14px",
              background: "linear-gradient(to right, #ffffff, #f9fafb)",
              outline: "none",
            }}
          />

          {/* Password */}
          <input
            type="password"
            value={password}
            onChange={(e) => setPwd(e.target.value)}
            placeholder="password"
            required
            style={{
              width: "100%",
              borderRadius: "6px",
              border: "1px solid #ccc",
              padding: "12px",
              fontSize: "14px",
              background: "linear-gradient(to right, #ffffff, #f9fafb)",
              outline: "none",
            }}
          />

          {/* Submit button */}
          <button
            type="submit"
            disabled={loading}
            style={{
              width: "100%",
              borderRadius: "6px",
              padding: "12px",
              fontSize: "16px",
              fontWeight: "500",
              background: "#f1c40f",
              color: "#000",
              cursor: loading ? "not-allowed" : "pointer",
              transition: "background 0.2s ease",
            }}
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        {/* Bottom link */}
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <span style={{ color: "#e5e7eb" }}>Not registered? </span>
          <a
            href="/register"
            style={{
              color: "#f1c40f",
              fontWeight: "600",
              textDecoration: "none",
            }}
          >
            Register now
          </a>
        </div>
      </div>
    </div>
  </div>
);

}
