import React, { useEffect, useState } from "react";
import api from "../api";
import Card from "../components/Card";
import Input, { Label } from "../components/Input";
import Button from "../components/Button";

export default function Profile() {
  const [u, setU] = useState({ username: "", email: "", first_name: "", last_name: "" });
  const [msg, setMsg] = useState("");

  useEffect(() => {
    api.get("/auth/profile/").then(({ data }) => setU(data));
  }, []);

  async function save(e) {
    e.preventDefault();
    await api.put("/auth/profile/", u);
    setMsg("Saved!");
    setTimeout(() => setMsg(""), 2000);
  }

  return (
    <div className="space-y-4">
      <h2 className="text-xl font-semibold">Profile</h2>
      <Card>
        <form onSubmit={save} className="grid md:grid-cols-2 gap-4">
          <div>
            <Label>Username</Label>
            <Input value={u.username || ""} onChange={(e)=>setU({...u, username:e.target.value})} />
          </div>
          <div>
            <Label>Email</Label>
            <Input value={u.email || ""} onChange={(e)=>setU({...u, email:e.target.value})} />
          </div>
          <div>
            <Label>First name</Label>
            <Input value={u.first_name || ""} onChange={(e)=>setU({...u, first_name:e.target.value})} />
          </div>
          <div>
            <Label>Last name</Label>
            <Input value={u.last_name || ""} onChange={(e)=>setU({...u, last_name:e.target.value})} />
          </div>
          <div className="md:col-span-2">
            <Button type="submit">Save</Button>
            {msg && <span className="ml-3 text-green-700">{msg}</span>}
          </div>
        </form>
      </Card>
    </div>
  );
}
