import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

// Drop-in replacement for your current Login.jsx matching the reference screenshots
export default function Login({ title = "Welcome to Employment Management System" }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, {
        username,
        password,
      });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      window.location.href = "/";
    } catch {
      setError("Invalid username or password");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen w-full bg-sky-500 grid place-items-center px-4 py-10">
      <div className="w-full max-w-xl">
        {/* Top brand bar like the screenshot */}
        <h1 className="text-center text-white text-3xl tracking-[0.35em] font-semibold drop-shadow-sm mb-6">
          {title}
        </h1>

        {/* Card */}
        <div className="bg-white rounded-md shadow-2xl border border-slate-200 overflow-hidden">
          {/* Thin blue strip across the top */}
          <div className="h-1.5 bg-sky-600" />

          <div className="p-8">
            <h2 className="text-center text-2xl font-light text-slate-700 mb-7">
              Sign in to your account
            </h2>

            {error && (
              <div
                role="alert"
                className="mb-5 rounded-md border border-red-300 bg-red-50 text-red-700 px-3 py-2 text-sm text-center"
              >
                {error}
              </div>
            )}

            {/* Form */}
            <form onSubmit={submit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-slate-700 mb-1">
                  Enter Username:
                </label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full bg-transparent py-2 border-b border-slate-300 focus:border-sky-600 focus:outline-none"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-700 mb-1">
                  Enter Password:
                </label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full bg-transparent py-2 border-b border-slate-300 focus:border-sky-600 focus:outline-none"
                  required
                />
              </div>

              <button
                type="submit"
                disabled={loading}
                className="w-full bg-sky-600 text-white font-medium py-2 rounded-md hover:bg-sky-700 transition disabled:opacity-60 disabled:cursor-not-allowed"
              >
                {loading ? "Signing in..." : "Sign In"}
              </button>
            </form>
          </div>

          {/* Footer strip like the screenshot */}
          <div className="bg-slate-100 py-4 text-center text-sm">
            New user?{" "}
            <a href="/register" className="text-sky-700 hover:underline font-medium">
              Register
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}
