// src/pages/Login.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

// small helper to get the input gradient look
const GradientInput = ({ type = "text", ...props }) => (
  <div className="rounded-md bg-gradient-to-b from-[#ffffff] to-[#d9d7ee] p-[3px] shadow-[0_2px_8px_rgba(0,0,0,0.15)]">
    <input
      type={type}
      className="w-full rounded-[6px] bg-transparent px-3 py-2 text-gray-900 placeholder-gray-500 focus:outline-none"
      {...props}
    />
  </div>
);

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPwd] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, {
        username: email, // if your backend expects "username"
        password,
      });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      window.location.href = "/";
    } catch {
      setError("Invalid credentials");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center px-4 bg-gradient-to-b from-[#d7e6f2] via-[#eaf1f7] to-[#d7e6f2]">
        <div className="w-full max-w-lg">
        {/* Card */}
        <div className="rounded-md shadow-2xl bg-[#0a0a6a] text-white p-8">
            
            {/* System Header */}
            <h2 className="text-center text-2xl font-bold mb-6">
            Welcome to Employment Management System
            </h2>

            {/* Login Title */}
            <h1 className="text-3xl font-bold mb-8">Login</h1>

            {error && (
            <div className="mb-4 rounded-md border border-red-300 bg-red-50 text-red-700 px-3 py-2 text-sm">
                {error}
            </div>
            )}

            <form onSubmit={submit} className="space-y-6">
            {/* Email */}
            <input
                type="text"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="email"
                autoComplete="username"
                required
                className="w-full rounded-lg border border-gray-300 px-3 py-3 text-black bg-gradient-to-r from-white to-gray-100 focus:outline-none focus:ring-2 focus:ring-yellow-400"
            />

            {/* Password */}
            <input
                type="password"
                value={password}
                onChange={(e) => setPwd(e.target.value)}
                placeholder="password"
                autoComplete="current-password"
                required
                className="w-full rounded-lg border border-gray-300 px-3 py-3 text-black bg-gradient-to-r from-white to-gray-100 focus:outline-none focus:ring-2 focus:ring-yellow-400"
            />

            {/* Submit button */}
            <button
                type="submit"
                disabled={loading}
                className="w-full rounded-lg bg-yellow-400 text-black font-semibold py-3 hover:bg-yellow-500 transition disabled:opacity-60"
            >
                {loading ? "Logging in..." : "Login"}
            </button>
            </form>

            {/* Bottom link */}
            <div className="mt-6 text-center">
            <span className="text-gray-200">Not registered? </span>
            <a
                href="/register"
                className="text-yellow-400 hover:underline font-medium"
            >
                Register now
            </a>
            </div>
        </div>
        </div>
    </div>
    );


}
