import React, { useState } from "react";
import api from "../api/api";
import Section from "../components/Section";
import PageHeader from "../components/PageHeader";
import Input from "../components/Input";
import Button from "../components/Button";
import FormField from "../components/FormField";

export default function ChangePassword() {
  const [f, setF] = useState({ old_password: "", new_password: "" });
  const [msg, setMsg] = useState("");

  async function submit(e) {
    e.preventDefault();
    await api.put("/auth/change-password/", f);
    setMsg("Password changed");
    setF({ old_password: "", new_password: "" });
    setTimeout(() => setMsg(""), 2000);
  }

  return (
    <div className="container-app page-narrow">
      <PageHeader title="Change Password" />
      <Section>
        <form onSubmit={submit} className="space-y-4">
          <FormField label="Old password"><Input type="password" value={f.old_password} onChange={e=>setF({...f, old_password:e.target.value})}/></FormField>
          <FormField label="New password"><Input type="password" value={f.new_password} onChange={e=>setF({...f, new_password:e.target.value})}/></FormField>
          <div className="flex items-center gap-3 justify-end">
            <Button>Save</Button>
            {msg && <span className="text-green-700">{msg}</span>}
          </div>
        </form>
      </Section>
    </div>
  );
}
