// src/pages/Register.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

export default function Register() {
  const [f, setF] = useState({ username: "", email: "", password: "" });
  const [msg, setMsg] = useState("");

  async function submit(e) {
    e.preventDefault();
    await axios.post(`${baseURL}/auth/register/`, f);
    setMsg("Registered. Please login.");
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-gray-100 via-gray-50 to-gray-100 px-4">
      <div className="w-full max-w-md">
        <div className="rounded-lg shadow-xl bg-white p-8">
          <h1 className="text-xl font-bold text-center mb-6">
            Create your account
          </h1>
          <h2 className="text-lg font-semibold text-center mb-6">Register</h2>

          {msg && (
            <div className="mb-4 rounded-md border border-green-300 bg-green-50 text-green-700 px-3 py-2 text-sm text-center">
              {msg}
            </div>
          )}

          <form onSubmit={submit} className="space-y-4">
            <input
              type="text"
              placeholder="Username"
              value={f.username}
              onChange={(e) => setF({ ...f, username: e.target.value })}
              className="w-full rounded-md border px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              required
            />

            <input
              type="email"
              placeholder="Email"
              value={f.email}
              onChange={(e) => setF({ ...f, email: e.target.value })}
              className="w-full rounded-md border px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              required
            />

            <input
              type="password"
              placeholder="Password"
              value={f.password}
              onChange={(e) => setF({ ...f, password: e.target.value })}
              className="w-full rounded-md border px-3 py-2 focus:ring-2 focus:ring-indigo-500 focus:outline-none"
              required
            />

            <button
              type="submit"
              className="w-full rounded-md bg-indigo-600 text-white font-medium py-2 hover:bg-indigo-700 transition"
            >
              Register
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-sm">
              Already have an account?{" "}
              <a href="/login" className="text-indigo-600 hover:underline">
                Login here
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
