// src/pages/Login.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

// small helper to get the input gradient look
const GradientInput = ({ type = "text", ...props }) => (
  <div className="rounded-md bg-gradient-to-b from-[#ffffff] to-[#d9d7ee] p-[3px] shadow-[0_2px_8px_rgba(0,0,0,0.15)]">
    <input
      type={type}
      className="w-full rounded-[6px] bg-transparent px-3 py-2 text-gray-900 placeholder-gray-500 focus:outline-none"
      {...props}
    />
  </div>
);

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPwd] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, {
        username: email, // if your backend expects "username"
        password,
      });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      window.location.href = "/";
    } catch {
      setError("Invalid credentials");
    } finally {
      setLoading(false);
    }
  }

  return (
  <div
    className="min-h-screen flex items-center justify-center px-4"
    style={{
      background:
        "linear-gradient(180deg, #d7e6f2 0%, #eaf1f7 55%, #d7e6f2 100%)",
    }}
  >
    <div className="w-full max-w-md">
      {/* Card */}
      <div className="rounded-lg shadow-lg bg-[#0a0a6a] text-white p-8 text-center">
        {/* Header */}
        <h1 className="text-2xl font-bold mb-2">
          Welcome to Employment Management System
        </h1>
        <h2 className="text-xl font-semibold mb-6">Login</h2>

        {error && (
          <div className="mb-4 rounded-md border border-red-300 bg-red-50 text-red-700 px-3 py-2 text-sm text-left">
            {error}
          </div>
        )}

        {/* Form */}
        <form onSubmit={submit} className="space-y-4">
          <input
            type="text"
            value={username}
            onChange={(e) => setU(e.target.value)}
            placeholder="Username"
            className="w-full rounded-md border border-gray-300 px-3 py-2 text-black focus:outline-none focus:ring-2 focus:ring-yellow-400"
            required
          />

          <input
            type="password"
            value={password}
            onChange={(e) => setP(e.target.value)}
            placeholder="Password"
            className="w-full rounded-md border border-gray-300 px-3 py-2 text-black focus:outline-none focus:ring-2 focus:ring-yellow-400"
            required
          />

          <button
            type="submit"
            disabled={loading}
            className="w-full rounded-md bg-yellow-400 text-black font-medium py-2 hover:bg-yellow-500 transition disabled:opacity-60"
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        {/* Links */}
        <div className="mt-4">
          <p className="text-sm">
            Not registered?{" "}
            <a href="/register" className="text-yellow-400 hover:underline">
              Register now
            </a>
          </p>
          <p className="text-sm mt-2">
            Forgot your password?{" "}
            <a href="/change-password" className="text-yellow-400 hover:underline">
              Change password
            </a>
          </p>
        </div>
      </div>
    </div>
  </div>
);

}
