// src/pages/Login.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

const IconUser = () => (
  <svg viewBox="0 0 24 24" className="w-4 h-4 text-gray-500">
    <path fill="currentColor" d="M12 12a5 5 0 1 0-5-5a5 5 0 0 0 5 5m0 2c-4.42 0-8 2.24-8 5v1a1 1 0 0 0 1 1h14a1 1 0 0 0 1-1v-1c0-2.76-3.58-5-8-5z"/>
  </svg>
);
const IconLock = () => (
  <svg viewBox="0 0 24 24" className="w-4 h-4 text-gray-500">
    <path fill="currentColor" d="M12 1a5 5 0 0 0-5 5v3H6a2 2 0 0 0-2 2v8a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2v-8a2 2 0 0 0-2-2h-1V6a5 5 0 0 0-5-5m0 2a3 3 0 0 1 3 3v3H9V6a3 3 0 0 1 3-3m-1 11h2v4h-2z"/>
  </svg>
);

export default function Login() {
  const [username, setU] = useState("");
  const [password, setP] = useState("");
  const [remember, setRemember] = useState(false);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, { username, password });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      if (!remember) {
        sessionStorage.setItem("access", data.access);
        sessionStorage.setItem("refresh", data.refresh);
      }
      window.location.href = "/";
    } catch {
      setError("Invalid username or password");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen bg-slate-100 flex items-center justify-center px-4">
      <div className="w-full max-w-md">
        {/* Card */}
        <div className="bg-white border rounded-xl shadow-sm overflow-hidden">
          <div className="h-2 bg-blue-500" />
          <div className="p-6 md:p-7">
            <h1 className="text-2xl font-semibold mb-6">Login</h1>

            {error && (
              <div className="mb-4 rounded-md border border-red-200 bg-red-50 text-red-700 px-3 py-2 text-sm">
                {error}
              </div>
            )}

            <form onSubmit={submit} className="space-y-4">
              {/* Username */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Username</label>
                <div className="relative">
                  <span className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <IconUser />
                  </span>
                  <input
                    type="text"
                    value={username}
                    onChange={(e) => setU(e.target.value)}
                    className="w-full rounded-md border border-gray-300 pl-9 pr-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Email or username"
                    autoComplete="username"
                    required
                  />
                </div>
              </div>

              {/* Password */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                <div className="relative">
                  <span className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <IconLock />
                  </span>
                  <input
                    type="password"
                    value={password}
                    onChange={(e) => setP(e.target.value)}
                    className="w-full rounded-md border border-gray-300 pl-9 pr-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Password"
                    autoComplete="current-password"
                    required
                  />
                </div>
              </div>

              {/* Remember + Forgot */}
              <div className="flex items-center justify-between">
                <label className="inline-flex items-center gap-2 text-sm text-gray-700">
                  <input
                    type="checkbox"
                    className="h-4 w-4"
                    checked={remember}
                    onChange={(e) => setRemember(e.target.checked)}
                  />
                  Keep me signed in
                  <span className="text-gray-400" title="Stores your session so you don't have to login every time">ⓘ</span>
                </label>
                <a href="/forgot-password" className="text-blue-700 hover:underline text-sm">
                  Forgot your password?
                </a>
              </div>

              {/* Submit */}
              <button
                type="submit"
                disabled={loading}
                className="inline-flex items-center gap-2 px-4 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-700 disabled:opacity-60"
              >
                {loading ? "Signing in..." : "Submit"}
              </button>
            </form>
          </div>

          {/* Language (optional for the look) */}
          <div className="px-6 pb-6">
            <div className="inline-flex items-center gap-2 border rounded-md px-3 py-2 text-sm text-gray-700 bg-gray-50">
              <span>English</span>
              <select className="bg-transparent focus:outline-none" defaultValue="en">
                <option value="en">English</option>
                <option value="hi">Hindi</option>
                <option value="ta">Tamil</option>
              </select>
            </div>
          </div>
        </div>

        {/* ---- Bottom CTA: Don't have an account? Create account ---- */}
        <div className="mt-4 text-center">
          <span className="text-gray-700">Don’t have an account?</span>
          <a
            href="/register"
            className="ml-2 inline-flex items-center px-3 py-2 rounded-md bg-blue-600 text-white hover:bg-blue-700 text-sm"
          >
            Create account
          </a>
        </div>
      </div>
    </div>
  );
}
