// src/pages/Login.jsx
import React, { useState } from "react";
import axios from "axios";
import { baseURL } from "../api/api";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function submit(e) {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const { data } = await axios.post(`${baseURL}/auth/login/`, {
        username,
        password,
      });
      localStorage.setItem("access", data.access);
      localStorage.setItem("refresh", data.refresh);
      window.location.href = "/";
    } catch {
      setError("Invalid username or password");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-sky-100 to-white px-4">
      <div className="w-full max-w-xs">
        <div className="rounded-md shadow-lg bg-[#00004d] p-8 text-white">
          <h2 className="text-2xl font-bold mb-6">Login</h2>

          {error && (
            <div className="mb-4 rounded-md border border-red-400 bg-red-100 text-red-700 px-3 py-2 text-sm">
              {error}
            </div>
          )}

          <form onSubmit={submit} className="space-y-4">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="email"
              required
              className="w-full rounded-md bg-gradient-to-b from-white to-gray-200 px-3 py-2 text-gray-900 placeholder-gray-500 focus:outline-none"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="password"
              required
              className="w-full rounded-md bg-gradient-to-b from-white to-gray-200 px-3 py-2 text-gray-900 placeholder-gray-500 focus:outline-none"
            />

            <button
              type="submit"
              disabled={loading}
              className="w-full rounded-md bg-yellow-400 text-black font-medium py-2 hover:bg-yellow-500 transition disabled:opacity-60"
            >
              {loading ? "Logging in..." : "Login"}
            </button>
          </form>

          <div className="mt-6 text-center text-sm">
            <p>
              Not registered?{" "}
              <a href="/register" className="text-yellow-400 hover:underline">
                Register now
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
